import { createApp } from "vue"; // Import đúng cú pháp của Vue 3
import App from "./App";
import router from "./router/index";
import PaperDashboard from "./plugins/paperDashboard";
import Toast from "vue-toastification";
import "vue-toastification/dist/index.css";

// Tạo và cấu hình ứng dụng
const app = createApp(App);
app.use(router); // Sử dụng router
app.use(PaperDashboard); // Sử dụng PaperDashboard (nếu cần thiết)
app.use(Toast); // Sử dụng Toast

// Mount ứng dụng
app.mount("#app");
